import React, { useState } from "react";

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState("");

  function saveTodo(todo) {
    setTodos([...todos, todo]);
    localStorage.setItem("todos", JSON.stringify(todos));
    setText(""); // 이부분을 통해서 input 창을 비워야 하는데 안됨.
  }

  function handelTodoSubmit(text) {
    const newTodoObj = { id: Date.now(), text: text };
  }

  function deleteTodo() {}

  return (
    <div>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          console.log(e);
          handelTodoSubmit(text);
        }}
      >
        <input
          type="text"
          placeholder="Write What to do and Press Enter."
          required
          onChange={(e) => setText(e.target.value)}
        />
      </form>

      <ul>
        {todos.map((todo, index) => (
          <li key={index}>
            {todo}
            <button
              onClick={(e) => {
                deleteTodo();
              }}
            >
              x
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
