import React, { useState } from "react";
import TodoLists from "./TodoLists";

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState("");
  const { id, todo } = todos;

  function saveTodos() {
    localStorage.setItem("todos", JSON.stringify(todos));
  }

  function deleteTodo(e) {
    console.log(e.target.parentNode.id);
    const id = e.target.parentNode.id;
    setTodos(todos.filter((todo) => todo.id !== id));
    localStorage.setItem("todos", JSON.stringify(todos));
  }

  function handleTodoSubmit(newTodo) {
    // console.log(newTodo);

    const newTodoObj = { id: String(Date.now()), todo: newTodo };
    setTodos([...todos, newTodoObj]);
    saveTodos();
    // setInput(""); // 이부분을 통해서 input 창을 비워야 하는데 안됨.
    // 이 부분을 넣으면 입력값을 넣어도 빈 문자열이 들어가는 경우가 생김.
    // 왜인지 잘 모르겠지만, 빈 문자열이 들어가는 경우는 없어짐
  }

  return (
    <div>
      <form>
        <input
          type="text"
          placeholder="Write What to do and Press Enter."
          required
          onChange={(e) => setInput(e.target.value)}
        />
      </form>

      <TodoLists todos={todos} deleteTodo={deleteTodo} />
    </div>
  );
}

export default TodoList;
