import React, { useState } from "react";

function TodoList({ todos, setTodos }) {
  function saveTodos() {
    localStorage.setItem("todos", JSON.stringify(todos));
  }
  return (
    <div>
      <form>
        <input
          type="text"
          placeholder="Write What to do and Press Enter."
          required
          onSubmit={(e) => {
            saveTodos();
          }}
        ></input>
        <ul>
          {todos.map((todo, index) => (
            <span>
              <li key={index}>
                {todo}
                <button>x</button>
              </li>
            </span>
          ))}
        </ul>
      </form>
    </div>
  );
}

export default TodoList;
