import React, { useState } from "react";

function TodoList() {
  const [todos, setTodos] = useState({
    id: "",
    todo: "",
  });
  const [input, setInput] = useState("");

  function saveTodos() {
    localStorage.setItem("todos", JSON.stringify(todos));
  }

  function handelTodoSubmit(input) {
    setInput(""); // 이부분을 통해서 input 창을 비워야 하는데 안됨.
    const newTodoObj = { id: Date.now(), todo: input };
    // setTodos([...todos, newTodoObj]);
    setTodos(todos.concat(newTodoObj));
    saveTodos();
  }

  //   function deleteTodo() {}

  return (
    <div>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          console.log(e);
          handelTodoSubmit(input);
        }}
      >
        <input
          type="text"
          placeholder="Write What to do and Press Enter."
          required
          onChange={(e) => setInput(e.target.value)}
        />
      </form>

      <ul>
        {todos.map((todo, index) => (
          <li key={index}>
            {todo}
            <button>x</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
